$( function()
	{
		// Automatically add the 'even' and 'odd' classes to table rows
		$('tbody > tr:nth-child(even)').addClass('even');
		$('tbody > tr:nth-child(odd)').addClass('odd');

		// If the page has a 'div.navpath' element, remove the Doxygen $title from the breadcrumb.
		$('div.navpath').each( function() {
			var elem = document.getElementById( 'doxtitle' );
			if( elem ) elem.parentNode.removeChild( elem );
		} );

		// Remove the nasty tables wrapping the letters generated by Doxygen in classes.html
		$('div.ah').each( function() {
			var div = $(this);
			var tbl = div.parent().parent().parent().parent();
			var cell = tbl.parent();
			cell.append( div.detach() );
			tbl.remove();
		} );
	}
);
